import React, { useState, useRef } from "react";
import { Client, AccountId, PrivateKey, TokenCreateTransaction, TokenMintTransaction } from "@hashgraph/sdk";
import operator from "./config.js";
import MyButton from "./components/MyButton.jsx";
import "./styles/App.css";

function App() {
	const operatorId = AccountId.fromString(operator.id);
	const operatorKey = PrivateKey.fromString(operator.pvkey);
	const client = Client.forTestnet().setOperator(operatorId, operatorKey);

	const [tokenId, setTokenId] = useState();

	async function tokenCreate() {
		console.log("- Creating token");

		const tokenCreateTx = new TokenCreateTransaction()
			.setTokenName("dAppDayToken")
			.setTokenSymbol("DDT")
			.setTreasuryAccountId(operatorId)
			.setInitialSupply(100)
			.setDecimals(0)
			.setSupplyKey(operatorKey)
			.freezeWith(client);
		const tokenCreateSign = await tokenCreateTx.sign(operatorKey);
		const tokenCreateSubmit = await tokenCreateSign.execute(client);
		const tokenCreateRec = await tokenCreateSubmit.getRecord(client);
		const id = tokenCreateRec.receipt.tokenId;
		setTokenId(id);

		let tokenCreationText;
		if (tokenId === undefined) {
			tokenCreationText = "";
			console.log(`Nothing  yet`);
		} else {
			tokenCreationText = `Created token with ID: ${tokenId}`;
			console.log(`Token ID ${tokenId}`);
		}
		return tokenCreationText;
	}

