import React, { useState } from "react";
import { Client, AccountId, PrivateKey, TokenCreateTransaction } from "@hashgraph/sdk";
import operator from "../config.js";
//

function CreateButton() {
	const [token, setToken] = useState();

	async function tokenCreate() {
		console.log("- Creating token");

		const operatorId = AccountId.fromString(operator.id);
		const operatorKey = PrivateKey.fromString(operator.pvkey);
		const client = Client.forTestnet().setOperator(operatorId, operatorKey);

		const tokenCreateTx = new TokenCreateTransaction()
			.setTokenName("dAppDayToken")
			.setTokenSymbol("DDT")
			.setTreasuryAccountId(operatorId)
			.setInitialSupply(100)
			.setDecimals(0)
			.freezeWith(client);
		const tokenCreateSign = await tokenCreateTx.sign(operatorKey);
		const tokenCreateSubmit = await tokenCreateSign.execute(client);
		const tokenCreateRec = await tokenCreateSubmit.getRecord(client);
		const tokenId = tokenCreateRec.receipt.tokenId;

		setToken(tokenId);

		console.log(`- Token ID: ${token}`);
	}

	let a;
	if (token === undefined) {
		a = "Hi";
	} else {
		a = "Bye";
	}

	return (
		<div>
			<button onClick={tokenCreate} className="cta-button connect-wallet-button">
				Create Tokens
			</button>
			<p>new text</p>
		</div>
	);
}
export default CreateButton;
